mapper.select:
select DISTINCT u.user_id as userId, u.username, u.email, u.mobile, u.status, u.create_time, u.create_user_id, u.role_ids as roleIds from sys_user u, sys_role s,sys_user_role syr where u.user_id=syr.user_id and syr.role_id=s.role_id and s.role_id !=3
AND u.username = #{sysUserDetail.username}
AND (u.create_user_id = #{sysUserDetail.createUserId} OR u.user_id = #{sysUserDetail.createUserId})
AND s.role_id = #{sysUserDetail.roleId}

select
user_id,username,password,email,salt,mobile,status,create_user_id,create_time,role_ids
as roleIds,token from sys_user where username = #{username}
