mapper.select:
SELECT * FROM sys_message_management a where a.delete_status=0 and a.enable=1 and a.content_type=#{vo.contentType}
and a.effect_time > #{vo.registerDate}
and a.effect_time <= now() and (a.failure_time >= now() or a.failure_time is null)
and ( (a.target_object =1 and find_in_set(#{vo.userAccount},a.user_account)>0)
OR (a.target_object =2 and find_in_set(#{item},a.hierarchy_ids)>0)
OR a.target_object =3) AND a.id NOT in ( SELECT message_id from sys_message_user where user_id=#{vo.userId} ) ORDER BY a.create_time DESC

    	SELECT a.id messageId,a.title,IFNULL(b.id,0) statusId ,IFNULL(b.`status`,0) status ,
    	a.create_time createTime 
			FROM sys_message_management a 
			LEFT JOIN sys_message_user b on a.id=b.message_id and b.user_id=#{vo.userId}
	    	where a.delete_status=0 and  a.enable=1 and a.content_type=#{vo.contentType}

	    		and a.title=#{vo.title}


	    		and a.effect_time > #{vo.registerDate}

	    	and a.effect_time <= now() and (a.failure_time >= now() or a.failure_time is null)
			and ( (a.target_object =1 and  find_in_set(#{vo.userAccount},a.user_account)>0)

				OR (a.target_object =2 and  find_in_set(#{item},a.hierarchy_ids)>0)

			OR a.target_object =3)
			AND IFNULL(b.delete_message,0)=0 
			ORDER BY b.`status`,a.create_time DESC

SELECT count(1) FROM sys_message_management a LEFT JOIN sys_message_user b on a.id=b.message_id and b.user_id=#{userId} where a.delete_status=0 and a.enable=1 and a.content_type=#{contentType}
and a.effect_time > #{registerDate}
and a.effect_time <= now() and (a.failure_time >= now() or a.failure_time is null) and ( (a.target_object =1 and find_in_set(#{userAccount},a.user_account)>0)
OR (a.target_object =2 and find_in_set(#{item},a.hierarchy_ids)>0)
OR a.target_object =3) AND IFNULL(b.delete_message,0)=0 AND IFNULL(b.`status`,0)=0
